import React, {useState} from "react";
import PostsItem from "./PostsItem";
import {Link} from 'react-router-dom'


const Posts = ({posts, setPosts, token}) => {

    const [searchTerm, setSearchTerm] = useState('');

    const filteredPosts = posts.filter((postSearch) => {
        console.log(postSearch)
        if(postSearch.title.toLowerCase() === searchTerm){
            setSearchTerm(searchTerm)
        } else if(postSearch.author.username.toLowerCase() === searchTerm) {
            setSearchTerm(searchTerm)
        } else if(postSearch.description.toLowerCase() === searchTerm) {
            setSearchTerm(searchTerm)
        } else if(postSearch.location.toLowerCase() === searchTerm) {
            setSearchTerm(searchTerm)
        } else if(postSearch.price.toLowerCase() === searchTerm) {
            setSearchTerm(searchTerm)
        } else {
            searchTerm;
        }
    })

    return (<>
    <input 
        type="text" 
        placeholder="Search"
        value={searchTerm}
        onChange={event => setSearchTerm(event.target.value)}/>
    <Link className="fluid ui button" to="/Posts/create">Create a Post</Link>
    <div id="posts">
        {posts.map((post) => {
            // console.log("hi", post)
            return <PostsItem 
                key={post._id} 
                post={post} 
                setPosts={setPosts}
                token={token}
                />
        })}
    </div>
    </>);
};

export default Posts;
