import React, { useState, useEffect } from 'react';
import { Home, Posts, AccountForm } from "./components";
import { Route, Switch, Link, useHistory } from "react-router-dom";
import { fetchPosts, fetchGuest } from './api/api';

const App = () => {

    const [posts, setPosts] = useState([]);
    const [token, setToken] = useState(
        window.localStorage.getItem("token") || ""
        );
    const [guest, setGuest] = useState(null);

    const history = useHistory()

    useEffect(() => {
        const getPosts = async () => {
            try {
                const result = await fetchPosts();
                setPosts(result);
                console.log(result)
            } catch(error) {
                console.error("There was an error fetching posts", error);
            }
        };
        getPosts();
    }, []);

    useEffect(() => {
        if (token) {
            const getGuest = async () => {

                // const {guest} = await fetchGuest(token);
                const response = await fetchGuest(token);
                console.log("USERNAME", response);
                setGuest(response.username);
            };
            getGuest();
        }
    }, [token])

    useEffect(() => {
        window.localStorage.setItem("token", token)
    }, [token])

    const logOut = () => {
        setToken('');
        setGuest(null);
        history.push('/')
    }

    return (
        <div>
            <nav>
                <Link to="/">
                    Home
                </Link>
                <Link to="/Posts">
                    Posts
                </Link>
                <div>
                    {token ? (
                        <button onClick={(event) => {logOut}}>Log Out</button>
                    ):(
                    <>
                        <Link to="/AccountForm/LogIn">
                            Log In
                        </Link>
                        <Link to="/AccountForm/register">
                            Sign Up
                        </Link>
                    </>
                    )}
                </div>
            </nav>
            <Switch>
                <Route exact path="/">
                    <Home guest={guest}/>
                </Route>
                <Route path="/Posts">
                    <Posts posts={posts}/>
                </Route>
                <Route path="/AccountForm/:action">
                    <AccountForm setToken={setToken}/>
                </Route>
            </Switch>
        </div>
    );
};

export default App;
